import expect from 'expect';
import * as segmentActions from './segmentActions';
import * as types from './actionTypes';

import thunk from 'redux-thunk';
import nock from 'nock';
import configureMockStore from 'redux-mock-store';


describe('Segment Actions', () => {
  describe('createSegmentSuccess', () => {
    it('should create a CREATE_SEGMENT_SUCCESS action', () => {

      const segment = {
        id: 'u-of-m-only',
        name: 'U of M Only',
        percentage: 100,
        countries: ['US'],
        institutions: ['umich.edu'],
        enabled: true
      };

      const expectedAction = {
        type: types.CREATE_SEGMENT_SUCCESS,
        segment: segment
      };

      const action = segmentActions.createSegmentSuccess(segment);

      expect(action).toEqual(expectedAction);
    });
  });
});

const middleware = [thunk];
const mockStore = configureMockStore(middleware);

describe('Async Actions', ()=> {
  afterEach(() => {
    nock.cleanAll();
  });

  it('should create BEGIN_AJAX_CALL and LOAD_SEGMENTS_SUCCESS when loading segments', done => {

    const expectedActions = [
      {type: types.BEGIN_AJAX_CALL},
      {type: types.LOAD_SEGMENTS_SUCCESS, body: {segments: [{
        id: 'u-of-m-only',
        name: 'U of M Only',
        percentage: 100,
        countries: ['US'],
        institutions: ['umich.edu'],
        enabled: true
      }]}}
    ];

    const store = mockStore({segments: []}, expectedActions);
    store.dispatch(segmentActions.loadSegments()).then(() => {
      const actions = store.getActions();
      expect(actions[0].type).toEqual(types.BEGIN_AJAX_CALL);
      expect(actions[1].type).toEqual(types.LOAD_SEGMENTS_SUCCESS);
      done();
    });
  });

});

