import * as types from './actionTypes';
import segmentApi from '../api/mockSegmentApi';
import {beginAjaxCall, ajaxCallError} from './ajaxStatusActions';

export function loadSegmentsSuccess(segments) {
  return {type: types.LOAD_SEGMENTS_SUCCESS, segments};
}

export function createSegmentSuccess(segment) {
  return {type: types.CREATE_SEGMENT_SUCCESS, segment};
}

export function updateSegmentSuccess(segment) {
  return {type: types.UPDATE_SEGMENT_SUCCESS, segment};
}

export function deleteSegmentSuccess(segment) {
  return {type: types.DELETE_SEGMENT_SUCCESS, segment};
}

export function loadSegments() {
  return function(dispatch){
    dispatch(beginAjaxCall());
    return segmentApi.getAllSegments().then(segments => {
      dispatch(loadSegmentsSuccess(segments));
    }).catch(error => {
      throw(error);
    });
  };
}

export function saveSegment(segment) {
  return function (dispatch, getState) {
    dispatch(beginAjaxCall());
    return segmentApi.saveSegment(segment)
      .then(savedSegment => {
        segment.id
          ? dispatch(updateSegmentSuccess(savedSegment))
          : dispatch(createSegmentSuccess(savedSegment));
      }).catch(error => {
        dispatch(ajaxCallError(error));
        throw(error);
      });
  };
}

export function deleteSegment(segment){
  return (dispatch, getState) => {
    dispatch(beginAjaxCall());
    return segmentApi.deleteSegment(segment)
      .then(deletedSegment => {
        dispatch(deleteSegmentSuccess(deletedSegment));
      }).catch(error => {
        dispatch(ajaxCallError(error));
        throw(error);
      });
  };
}

