import delay from './delay';

// This file mocks a web API by working with the hard-coded data below.
// It uses setTimeout to simulate the delay of an AJAX call.
// All calls return promises.
const segments = [
  {
    id: 'u-of-m-only',
    name: 'U of M Only',
    percentage: 100,
    countries: ['US'],
    institutions: ['umich.edu'],
    userId: 'kristi-sitarski',
    enabled: true
  },
  {
    id: 'wcc-only',
    name: 'WCC Only',
    percentage: 100,
    countries: ['US'],
    institutions: ['wcc.edu'],
    userId: 'michael-vath',
    enabled: true
  },
  {
    id: 'emu-only',
    name: 'EMU Only',
    percentage: 100,
    countries: ['US'],
    institutions: ['emich.edu'],
    userId: 'sean-mitchey',
    enabled: true
  }
];

//This would be performed on the server in a real app. Just stubbing in.
const generateId = (segment) => {
  return segment.name().toLowerCase().split(' ').join('-');
};

class SegmentApi {
  static getAllSegments() {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        resolve(Object.assign([], segments));
      }, delay);
    });
  }

  static saveSegment(segment) {
    segment = Object.assign({}, segment); // to avoid manipulating object passed in.
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Simulate server-side validation
        const minSegmentNameLength = 3;
        if (segment.name.length < minSegmentNameLength) {
          reject(`Name must be at least ${minSegmentNameLength} characters.`);
        }
        if (segments.filter(s => s.name === segment.name).filter(s => s.id !== segment.id).length > 0){
          reject(`Name must be unique.`);
        }
        if (segment.id) {
          const existingSegmentIndex = segments.findIndex(s => s.id == segment.id);
          segments.splice(existingSegmentIndex, 1, segment);
        } else {
          //Just simulating creation here.
          //Cloning so copy returned is passed by value rather than by reference.
          segment.id = generateId(segment);
          segments.push(segment);
        }

        resolve(segment);
      }, delay);
    });
  }

  static deleteSegment(segmentToDelete) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const indexOfSegmentToDelete = segments.findIndex(segment => {
          segment.id == segmentToDelete.id;
        });
        segments.splice(indexOfSegmentToDelete, 1);
        resolve();
      }, delay);
    });
  }
}

export default SegmentApi;

